sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // Install submit guard once (also runs on load with isLoading=true)
    if (isLoading || isTemplate) {
        ensureSubmitGuard();
        return;
    }

    // Clear any previous messages on priority
    g_form.hideFieldMsg('priority', true);

    // When priority becomes 1, show prominent message
    if (newValue === '1') {
        g_form.showFieldMsg(
            'priority',
            'P1 selected. Follow the P1 runbook. You will be asked to confirm before saving.',
            'error',
            true
        );
    }

    function ensureSubmitGuard() {
        if (window._p1SubmitGuardInstalled) return;
        window._p1SubmitGuardInstalled = true;

        g_form.addSubmitCheck(function () {
            if (g_form.getValue('priority') === '1' && !window._p1Confirmed) {
                var gm = new GlideModal('glide_confirm_standard'); // UI Macro
                gm.setTitle('Confirm P1 Save');
                gm.setPreference('title', 'Confirm P1 Save');
                gm.setPreference('question', 'Priority is P1. Confirm you have followed the P1 runbook before saving.');
                gm.setPreference('onPromptComplete', function (answer) {
                    if (answer === true) {
                        window._p1Confirmed = true;
                        g_form.submit(); // re-trigger submit after confirmation
                    }
                });
                gm.render();
                return false; // block the current submit until user responds
            }
            return true;
        });
    }
}
