sys_script_include.script:
var IncidentReminder = Class.create();
IncidentReminder.prototype = {
    initialize: function () {},

    sendReminder: function (incidentSysId, message, remindingUser) {
        try {
            if (!incidentSysId) {
                return { success: false, message: 'incidentSysId is required' };
            }

            var inc = new GlideRecord('incident');
            if (!inc.get(incidentSysId)) {
                return { success: false, message: 'Incident not found' };
            }

            var note = message || 'Reminder';

            // Add a secure work note
            inc.setValue('work_notes', note);
            inc.update();

            // Notify assigned_to, if present
            var assignedTo = inc.getValue('assigned_to');
            if (assignedTo) {
                var fromUser = remindingUser || gs.getUserID();
                var senderDisplay = '';
                try {
                    var usr = new GlideRecord('sys_user');
                    if (fromUser && usr.get(fromUser)) {
                        senderDisplay = usr.getDisplayValue();
                    } else {
                        senderDisplay = gs.getUserName();
                    }
                } catch (e1) {
                    senderDisplay = gs.getUserName();
                }
                try {
                    // Prefer a namespaced event for notifications
                    gs.eventQueue('x_1731528_incide_1.incident.reminder', inc, note, senderDisplay);
                } catch (e) {
                    // Optional fallback to GlideNotification if available in this instance/scope
                    try {
                        if (typeof GlideNotification === 'function') {
                            var gn = new GlideNotification();
                            if (typeof gn.setFromUser === 'function') gn.setFromUser(fromUser);
                            if (typeof gn.setSubject === 'function') gn.setSubject('Incident Reminder: ' + inc.getDisplayValue('number'));
                            if (typeof gn.setBody === 'function') gn.setBody('From: ' + senderDisplay + '\n\n' + note);
                            if (typeof gn.setRecipients === 'function') gn.setRecipients(assignedTo);
                            if (typeof gn.send === 'function') gn.send();
                        }
                    } catch (ignore) {}
                }
            }

            return { success: true, message: 'Reminder added and notification queued.' };
        } catch (ex) {
            return { success: false, message: String(ex) };
        }
    },

    type: 'IncidentReminder'
};
