sys_script_include.script:
var ResolveIncidentAjax = Class.create();
ResolveIncidentAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    /**
     * Resolves an incident with the provided close code and notes
     * @returns {string} 'success' or error message
     */
    resolveIncident: function() {
        try {
            var incidentId = this.getParameter('sysparm_sys_id');
            var closeCode = this.getParameter('sysparm_close_code');
            var closeNotes = this.getParameter('sysparm_close_notes');

            // Input validation
            if (!incidentId || !closeCode) {
                return 'Missing required parameters';
            }

            var incident = new GlideRecord('incident');
            if (!incident.get(incidentId)) {
                return 'Incident not found';
            }

            // Check if already resolved/closed
            if (incident.state == 6 || incident.state == 7) {
                return 'Incident is already in a closed/resolved state';
            }

            // Update incident
            incident.state = 6; // 6 = Resolved
            incident.close_code = closeCode;
            incident.close_notes = closeNotes;
            incident.closed_at = new GlideDateTime();
            incident.closed_by = gs.getUserID();

            var incidentId = incident.update();

            if (incidentId) {
                return 'success';
            } else {
                return 'Failed to update incident';
            }

        } catch (ex) {
            gs.error('Error in ResolveIncidentAjax.resolveIncident: ' + ex.message);
            return 'An error occurred while resolving the incident';
        }
    },

    type: 'ResolveIncidentAjax'
});
