sys_ui_action.client_script_v2:
function onClick(g_form) {
    // Validate close code is selected
    var closeCode = g_form.getValue('close_code');
    if (!closeCode) {
        g_form.showFieldMsg('close_code', 'Please select a close code', 'error');
        return false;
    }

    // Validate close notes are provided
    var closeNotes = g_form.getValue('close_notes');
    if (!closeNotes) {
        g_form.showFieldMsg('close_notes', 'Please enter close notes', 'error');
        return false;
    }

    // Show confirmation dialog
    if (confirm('Are you sure you want to resolve this incident?')) {
        // Disable button to prevent double-clicks
        g_form.setDisabled('resolve_incident', true);

        // Call server-side script
        var ga = new GlideAjax('ResolveIncidentAjax');
        ga.addParam('sysparm_name', 'resolveIncident');
        ga.addParam('sysparm_sys_id', g_form.getUniqueValue());
        ga.addParam('sysparm_close_code', closeCode);
        ga.addParam('sysparm_close_notes', closeNotes);

        ga.getXMLAnswer(function(response) {
            if (response === 'success') {
                g_form.addInfoMessage('Incident has been resolved successfully.');
                // Refresh the form to show updated state
                window.location.reload(true);
            } else {
                g_form.addErrorMessage('Failed to resolve incident: ' + response);
                g_form.setDisabled('resolve_incident', false);
            }
        });
    }

    return false; // Prevent form submission
}

